package com.gridsum.weblog.parser.md.scala.enum

object PlatformType extends Enumeration {
  type PlatFormType = Value
  //iOS
  val IOS = Value("iOS")
  //Android
  val ANDROID = Value("Android")
  //WP
  val WP = Value("WP")
  //Windows RT
  val WINDOWS_RT = Value("Windows RT")

  def fromValue(value: Int): PlatFormType = {
    value match {
      case 1 =>
        return IOS
      case 2 =>
        return ANDROID
      case 3 =>
        return WP
      case 4 =>
        return WINDOWS_RT
      case _ =>
        return null
    }
  }
}
