package com.gridsum.spark.wd

import org.apache.hadoop.hbase.HBaseConfiguration
import org.slf4j.LoggerFactory

import com.gridsum.spark.wd.external.HBaseUserVisitInfoComponent
import com.gridsum.spark.wd.session.{SessionDataComponent, SessionGenerator, UserSessionDataAggregator}
import com.gridsum.wd.metadata.model.{ProfileType, ProfileConfig}
import com.gridsum.weblog.parser.wd.scala.configuration.DefaultWdProfileConfigService
import com.gridsum.weblog.parser.wd.scala.dataobject._

/**
  * process all sessions in an individual partition.
  * @param index RDD partition index.
  * @param dataPerProfileUser iterator of the partition collection.
  *                           each item is a key-value pair, with key as instance of CombinedId,
  *                           and value as all WdBaseDataObjects belonged to the user.
  * @param outputBasePath the base output path for writing DataObject Avro/Parquet files.
  *                       the absolute path is:
  *                       hdfs://$outputPath/$DataObject/year=$year/month=$month/day=$day/part-r-index.parquet
  * @param dateStr the date for which day's date to process, in the format of 'yyyyMMdd'
  * @param hadoopConfigProvider the configuration object passed in, all instances of PartitionProcessor
  *                             will use the same instance
  */
class PartitionProcessor(
    index: Int,
    dataPerProfileUser: Iterator[(CombinedId, Iterable[WdBaseDataObject])],
    outputBasePath: String,
    dateStr: String,
    hadoopConfigProvider: HadoopConfigurationProvider,
    profileConfigMap: Map[Int, ProfileConfig],
    sourceUserTable: Option[String],
    eqIdMap: Map[String, String])
    extends SessionDataComponent
    with ParquetOutputComponent
    with HBaseUserVisitInfoComponent
    with SessionGenerator {
  self =>

  import PartitionProcessor._

  private val wdProfileConfigService = new DefaultWdProfileConfigService(
      profileConfigMap)

  type Config = AllConfig

  // wire in the actual configuration information for all the components here
  trait AllConfig extends HBaseConfig with ParquetOutputConfig {
    // the actual hbase configuration information
    override val hadoopConfiguration = hadoopConfigProvider.hadoopConfiguration

    override val hbaseConfiguration =
      HBaseConfiguration.create(hadoopConfiguration)

    override val sourceUserTableOpt = sourceUserTable

    // the actual parquet output configuration information
    override val partitionIndex = index

    override val outputBaseDir = outputBasePath

    override val date = dateStr
  }

  object AllConfig extends AllConfig

  override def config = AllConfig

  // Server session ID, composed of 12 bits (0 ~ 4095) from partition index and
  // 52 bits from DateTime microseconds, which is enough until Year 2100
  private var serverSessionIdStart =
    (index.toLong << 52) + System.currentTimeMillis() * 1000

  def run(): Unit = {
    if (index > 4095) {
      // Chu Chao: Shall we continue in this case?
      logger.warn(
          s"Partition number exceeds 12 bits (it's ${index} now), which may cause ServerSessionId collide.")
    }

    logger.info(
        s"Partition index ${index} -- about to process the data for the current partition.")

    val batchSize = Integer.getInteger("wd.etl.UserMutateBatchSize", 512)
    dataPerProfileUser.grouped(batchSize) foreach { currentBatch =>
      logger.info(
          s"Partition index ${index} -- processing a batch of ${currentBatch.size} user(s).")

      val idAndFirstServerTimePairs =
        currentBatch map {
          case (profileUser, dataObjects) =>
            profileUser -> dataObjects
              .minBy(_.getServerTime.getTime)
              .getServerTime
              .getTime
        }

      val usersVisitInfo = retrieveUsersVisitInfo(idAndFirstServerTimePairs)

      val usersInfoToPersist =
        currentBatch map {
          case (profileUser, dataObjects) =>
            val profileGroup =
              wdProfileConfigService.getProfileGroup(profileUser.profileId)
            val conversionPathConfig =
              wdProfileConfigService.getConversionPathConfigsByProfileId(
                  profileUser.profileId)
            val isMobileProfile =
              wdProfileConfigService.getProfileType(profileUser.profileId) == ProfileType.MOBILE
            val aggregator =
              new UserSessionDataAggregator(profileUser,
                                            serverSessionIdStart,
                                            usersVisitInfo.get(profileUser),
                                            eqIdMap,
                                            isMobileProfile)

            val objectSeq = dataObjects.toSeq

            val sessionData = {
              if (isMobileProfile) groupDataObjectsForMobile(objectSeq)
              else groupDataObjects(objectSeq)
            }

            val (visitInfo, records) =
              aggregator.aggregate(sessionData, conversionPathConfig)

            // update the 'the start of the session id' for allocating the ids in the following rounds
            serverSessionIdStart += sessionData.size

            // write the data records
            writeDataRecords(profileGroup, records)

            visitInfo
        }

      logger.info(
          s"Partition index ${index} -- trying to persist the visit stats for ${usersInfoToPersist.size} users.")
      // persist the users visit statistics back
      updateUsersVisitInfo(usersInfoToPersist)

      logger.info(
          s"Partition index ${index} -- done with the current batch (#user = ${currentBatch.size}).")
    }
  }
}

object PartitionProcessor {
  private val logger = LoggerFactory.getLogger(classOf[PartitionProcessor])
}

